mapper.insert:

	SELECT NVL(MAX(BOARD_NO), 0) + 1 FROM BOARDS

INSERT INTO BOARDS(
BOARD_NO
, BOARD_TITLE
, MEMBER_ID
, CATEGORY
, WRITE_DATE
, BOARD_TEXT
, VIEW_COUNT

	, ATTACH_FILE

)
VALUES(
#{boardNo}
, #{boardTitle}
, #{memberId}
, #{category}
, sysdate
, #{boardText}
, '0'

	, #{attachFile}

)

mapper.update:

UPDATE BOARDS SET
    BOARD_TITLE = #{boardTitle}
    , BOARD_TEXT = #{boardText} 



	, ATTACH_FILE = #{attachFile}



WHERE BOARD_NO = #{boardNo}


UPDATE BOARDS SET
	BOARD_NO = #{boardNo}
    , BOARD_TITLE = #{boardTitle}
    , BOARD_TEXT = #{boardText}



	, ATTACH_FILE = #{attachFile}



WHERE BOARD_NO = #{boardNo}

UPDATE BOARDS SET
VIEW_COUNT = VIEW_COUNT + 1
WHERE BOARD_NO = #{boardNo}

mapper.select:
		SELECT * FROM (
		SELECT A.*, ROWNUM RNUM FROM (
		SELECT BOARD_NO
		,BOARD_TITLE
		,MEMBER_ID
		,CATEGORY
		,WRITE_DATE
		,BOARD_TEXT
		,VIEW_COUNT
		,ATTACH_FILE
		FROM BOARDS
		WHERE CATEGORY = #{category}

			AND MEMBER_ID = #{memberId}

		ORDER BY BOARD_NO DESC

	) A WHERE ROWNUM <= #{lastIndex}
)WHERE RNUM > #{firstIndex}

SELECT COUNT(*)
FROM BOARDS
WHERE CATEGORY = #{category}

	AND MEMBER_ID = #{memberId}

SELECT BOARD_NO ,BOARD_TITLE ,MEMBER_ID ,CATEGORY ,WRITE_DATE ,BOARD_TEXT ,VIEW_COUNT ,ATTACH_FILE FROM BOARDS WHERE CATEGORY = #{category}
AND MEMBER_ID = #{memberId}
ORDER BY BOARD_NO DESC
