mapper.update:
        UPDATE BROADCASTS SET

        	  BROADCAST_TITLE = #{broadcastTitle}
       		, CONTENT_CODE = #{contentCode}

	        	, CHANNEL_ID = #{channelId}
	        	, STREAM_URL = #{streamUrl}
        		, BROADCAST_STATUS = #{broadcastStatus}
	        	, START_DATE = SYSDATE



        	  CHANNEL_ID = ''
        	, STREAM_URL = ''
        	, BROADCAST_STATUS = #{broadcastStatus}

        WHERE BROADCAST_NO = #{broadcastNo}

mapper.select:
SELECT BROADCAST_NO , BROADCAST_TITLE , CHANNEL_ID , STREAM_URL , START_DATE , BROADCAST_STATUS , CONTENT_CODE , MEMBER_ID , (SELECT NICK_NAME FROM MEMBERS WHERE MEMBER_ID = #{memberId}) as "nickName" , (SELECT MEMBER_IMAGE FROM MEMBERS WHERE MEMBER_ID = #{memberId}) as "memberImage" FROM BROADCASTS WHERE 1=1
AND BROADCAST_NO = #{broadcastNo}
AND MEMBER_ID = #{memberId}
