mapper.insert:
INSERT INTO MEMBERS(MEMBER_ID
					, PASSWORD
					, NICK_NAME
					, MEMBER_NAME
					, EMAIL
					, TEL

					, MEMBER_IMAGE

					) 
VALUES(#{memberId}
		, #{password}
		, #{nickName}
		, #{memberName}
		, #{email}
		, #{tel}

		, #{memberImage})

mapper.update:
UPDATE MEMBERS SET
		PASSWORD = #{password}
        , NICK_NAME = #{memberName}
        , EMAIL = #{email}
        , TEL = #{tel}
        , MEMBER_IMAGE = #{memberImage}
        , BANK = #{bank}
WHERE MEMBER_ID = #{memberId}

UPDATE MEMBERS SET
		POINT = POINT + #{chargePoint}
WHERE MEMBER_ID = #{memberId}

UPDATE MEMBERS SET
		PASSWORD = #{password}
        , EMAIL = #{email}
        , POINT = #{point}
WHERE MEMBER_ID = #{memberId}
